function addJoke() {
    const jokeInput = document.getElementById("joke-input");
    const jokeBox = document.getElementById("joke-box");
    const jokeText = document.getElementById("joke-text");

    if (jokeInput.value.trim() === "") {
        alert("Please enter a joke!");
        return;
    }

    jokeText.innerHTML = `<p>${jokeInput.value}</p>`;
    jokeBox.style.display = "block";
    jokeInput.value = "";
}
